import React, { useEffect, useState } from 'react';
import { View, Text, Image, TouchableOpacity, StyleSheet, Modal, FlatList } from 'react-native';
import axios from 'axios';

export default function Home({ route, navigation }) {
  const [modalVisible, setModalVisible] = useState(false);
  const [user, setUser] = useState({
    mail: null,
    password: null,
  });
  const [personajes, setPersonajes] = useState([]);

  useEffect(() => {
    const storedMail = localStorage.getItem('mail');
    const storedPass = localStorage.getItem('pass');
    setUser({ mail: storedMail, password: storedPass });
  }, []);

  // api Marvel
  useEffect(() => {
    axios.get('http://gateway.marvel.com/v1/public/comics?ts=1&apikey=0f34133c1783016d6d25e28f6e704f10&hash=013f4ebfd13ba730cf1ba5f735a4bb88')
      .then((res) => {
        setPersonajes(res.data.data.results);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>Marvel Info</Text>
        <View style={styles.buttonContainer}>
          <TouchableOpacity style={styles.arenaContainer} onPress={() => navigation.navigate('Home')}>
            <Text style={styles.buttonText}>Go back</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.arenaContainer} onPress={() => navigation.navigate('Comics')}>
            <Text style={styles.buttonText}>Comics</Text>
          </TouchableOpacity>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'black',
  },
  header: {
    backgroundColor: '#3498db',
    padding: 20,
    alignItems: 'center',
  },
  buttonContainer: {
    width:'50vw',
    flexDirection: 'row', // Muestra los elementos en una fila
    justifyContent: 'space-between', // Espacio uniforme entre los elementos
    marginTop: 20,
  },
  buttonText: {
    color: '#3498db',
    margin:1,
    fontSize: 16,
    fontWeight:'bold'
  },
  headerText: {
    color: '#fff',
    fontSize: 24,
    fontWeight: 'bold',
  },
  body: {
    flex: 1,
    padding: 20,
  },
  cardContainer: {
    borderWidth: '2px',
    borderColor: 'white',
    backgroundColor: 'black',
    borderRadius: 10,
    padding: 20,
    marginBottom: 20,
    alignItems: 'center',
    flex: 1,
    margin: 5,
  },
  cardImage: {
    width: 150,
    height: 150,
    resizeMode: 'cover',
    marginBottom: 10,
  },
  cardDescription: {
    textAlign: 'center',
    color: 'white',
    fontSize: 16,
  },
  arenaContainer: {
    backgroundColor: '#fff',
    borderRadius: 10,
    padding: 5,
    alignItems: 'center',
    marginTop: 20,
  },
  arenaText: {
    color: '#333',
    fontSize: 18,
    fontWeight: 'bold',
  },
  modalContainer: {
    alignItems: 'flex-end',
    margin: 10,
    height: '40%',
    borderWidth: 2,
    backgroundColor: 'white',
    borderRadius: 1,
  },
  closeButton: {
    margin: 10,
    textAlign: 'right',
    alignSelf: 'flex-start',
    fontSize: 20,
  },
  modalText: {
    margin: 10,
    alignSelf: 'center',
    fontSize: 20,
    fontWeight: 'bold',
  },
});
