import React, { useState } from 'react';
import { StatusBar } from 'expo-status-bar';
import { Button, StyleSheet, Text, View, TouchableOpacity, Dimensions, TextInput } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { getAuth, signInWithEmailAndPassword } from 'firebase/auth'
import axios from 'axios';

export default function Login() {
  const [usuario,setUsuario] = useState('');
  const [password,setPass] = useState('');
  const navigation = useNavigation();
    const [user, setUser] = useState({
      Email:'',
      Usuario: '',
      Password: '',
      Nombre: usuario,
      Apellido: ''});
  
  //  let obj;

  const auth = getAuth()

   /*const submitForm = () => {
       obj = {
        Nombre:"",
        Apellido:"",
        Usuario: usuario,
        Password: password
      }
      console.log(obj);
      axios.post('http://localhost:5000/login', obj ,{
      })
      .then((res) => {
          console.log(res.data.message);
          //(res.data.message != "usuario no registrado!") ? navigation.navigate('Home', { user: obj });
          if(res.data.message != "user not found"){navigation.navigate('Home',{ user:obj});}
      })
      .catch(console.log("usuario no registrado!"))
  };*/


  const login = () => {
    const obj = {
      Email:"",
      Nombre:"",
      Apellido:"",
      Usuario: usuario,
      Password: password
    }

    signInWithEmailAndPassword(auth, usuario, password)
    .then((userCredential) => {
      const userLogged = userCredential.user;
      const updateUserContext = async () => await setUser(userLogged)
      updateUserContext()
      console.log('Usuario autenticado')
      navigation.navigate('Home', { user: obj })
    })
    .catch((error) => {
      console.log(error)
      console.log('Usuario no correcto');
    })
  }


  return (
    <View style={styles.container}>
      <Text style={styles.title}>Bienvenido</Text>
      <TextInput
        style={styles.input}
        placeholder="Ingrese su usuario"
        value={usuario}
        onChangeText={(text) => setUsuario(text)}
      />
      <TextInput
        style={styles.input}
        placeholder="Ingrese su contraseña"
        secureTextEntry
        value={password}
        onChangeText={(text) => setPass(text)}
      />
      <TouchableOpacity style={styles.button} onPress={login}>
        <Text style={styles.buttonText}>Iniciar Sesión</Text>
      </TouchableOpacity>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f0f0f0',
    alignItems: 'center',
    justifyContent: 'center',
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 20,
  },
  input: {
    width: '50%',
    height: 50,
    borderWidth: 2,
    borderColor: '#ccc',
    borderRadius: 10,
    marginBottom: 20,
    padding: 10,
  },
  button: {
    width: Dimensions.get('window').width * 0.4,
    height: 50,
    borderRadius: 10,
    backgroundColor: 'black',
    alignItems: 'center',
    justifyContent: 'center',
  },
  buttonText: {
    color: 'white',
    fontSize: 18,
  },
});
